function getOutgoingMessageByIdQuery(apiBase: string) {
  return mockGetOutgoingMessageByIdQuery(async ({ variables }) => {
    const id = variables.documentId;
    const esettOutgoingMessageById = eSettDetailedExchangeEvents(apiBase).find(
      (x) => x.documentId === id
    );

    await delay(mswConfig.delay);
    return esettOutgoingMessageById
      ? HttpResponse.json({ data: { __typename: 'Query', esettOutgoingMessageById } })
      : HttpResponse.json({ data: null }, { status: 404 });
  });
}

function getOutgoingMessagesQuery() {
  return mockGetOutgoingMessagesQuery(async () => {
    await delay(mswConfig.delay);
    return HttpResponse.json(
      {
        data: {
          __typename: 'Query',
          esettExchangeEvents: {
            __typename: 'EsettExchangeEventsCollectionSegment',
            totalCount: eSettExchangeEvents.length,
            gridAreaCount: 1,
            pageInfo: {
              __typename: 'CollectionSegmentInfo',
              hasNextPage: true,
              hasPreviousPage: false,
            },
            items: eSettExchangeEvents,
          },
        },
      },
      { status: getStatus() }
    );
  });
}

function getBalanceResponsibleMessagesQuery(apiBase: string) {
  return mockGetBalanceResponsibleMessagesQuery(async () => {
    await delay(mswConfig.delay);
    const messages = eSettBalanceResponsibleMessages(apiBase);
    return HttpResponse.json(
      {
        data: {
          __typename: 'Query',
          balanceResponsible: {
            __typename: 'BalanceResponsiblePageResult',
            balanceResponsiblesUrl: `${apiBase}/v1/EsettExchange/DownloadBalanceResponsibles`,
            totalCount: messages.length,
            page: messages,
          },
        },
      },
      { status: getStatus() }
    );
  });
}

